generators.template_text:
security {
    macsec {
        connectivity-association DCI-link {
            cipher-suite gcm-aes-xpn-128;
            security-mode static-cak;
            include-sci;
            pre-shared-key {
                ckn {{ckn}};
                cak {{cak}};
            }
        }
        interfaces {
            {{interface}} {
                connectivity-association DCI-link;
            }
        }
    }
}
